mapper.select:
SELECT SUD.User_id As userId ,SDI.Db AS db ,SDI.Db_ver AS dbVer ,SDI.Db_period AS dbPeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyDBInfo SDI ON SUD.Career_id = SDI.Career_id WHERE SUD.Delete_flg = '0'
AND SDI.Db = #{db}
AND SDI.Db_ver LIKE #{dbVer}'%'
AND SDI.Db_period >= #{dbPeriod}
AND SDI.Db_period = #{dbPeriod}

SELECT SUD.User_id As userId ,SFI.Fw_nw AS fwNw ,SFI.Fw_nw_ver AS fwNwVer ,SFI.Fw_nw_period AS fwNwPeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyFwNwInfo SFI ON SUD.Career_id = SFI.Career_id WHERE SUD.Delete_flg = '0'
AND SFI.Fw_nw = #{fwNw}
AND SFI.Fw_nw_ver LIKE #{fwNwVer}'%'
AND SFI.Fw_nw_period >= #{fwNwPeriod}
AND SFI.Fw_nw_period = #{fwNwPeriod}

SELECT SUD.User_id As userId ,SOI.Os AS os ,SOI.Os_ver AS osVer ,SOI.Os_period AS osPeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyOSInfo SOI ON SUD.Career_id = SOI.Career_id WHERE SUD.Delete_flg = '0'
AND SOI.Os = #{os}
AND SOI.Os_ver LIKE #{osVer}'%'
AND SOI.Os_period >= #{osPeriod}
AND SOI.Os_period = #{osPeriod}

SELECT SUD.User_id As userId ,SLI.Language AS language ,SLI.Language_ver AS languageVer ,SLI.Language_period AS languagePeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyLanguageInfo SLI ON SUD.Career_id = SLI.Career_id WHERE SUD.Delete_flg = '0'
AND SLI.Language = #{language}
AND SLI.Language_ver LIKE #{languageVer}'%'
AND SLI.Language_period >= #{languagePeriod}
AND SLI.Language_period = #{languagePeriod}

SELECT SUD.User_id AS userId ,SOI.Other AS other ,SOI.Other_ver AS otherVer ,SOI.Other_period AS otherPeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyOtherInfo SOI ON SUD.Career_id = SOI.Career_id WHERE SUD.Delete_flg = '0'
AND SOI.Other = #{other}
AND SOI.Other_ver LIKE #{otherVer}
AND SOI.Other_period >= #{otherPeriod}
AND SOI.Other_period = #{otherPeriod}

mapper.update:
UPDATE
    StarrilyUserDetailInfo
SET
    Delete_flg = '1',
    Update_date = CURRENT_TIMESTAMP()
WHERE
    Career_id = #{careerId}
    AND User_id = #{userId}
    AND Update_date  <=  #{updateDate}
